std::string markdown_to_html(const std::string& markdown) {
    std::string html;

    // Replace markdown links with html links
    std::regex link_regex("((https?|ftp|file)://[-A-Za-z0-9+&@#/%?=~_|!:,.;]+[-A-Za-z0-9+&@#/%=~_|])");
    html = std::regex_replace(markdown, link_regex, "<a href=\"$1\">$1</a>");

    // Replace markdown headers with html headers
    std::regex header_regex("^([#]+)(.*)");
    std::smatch match;
    while (std::regex_search(markdown, match, header_regex)) {
        html += "<h" + std::to_string(match[1].length()) + ">";
        html += match[2];
        html += "</h" + std::to_string(match[1].length()) + ">";
        markdown = match.suffix();
    }

    // Replace markdown bold text with html bold text
    std::regex bold_regex("\\*\\*(.*?)\\*\\*");
    html = std::regex_replace(html, bold_regex, "<strong>$1</strong>");

    // Replace markdown italic text with html italic text
    std::regex italic_regex("\\*(.*?)\\*");
    html = std::regex_replace(html, italic_regex, "<em>$1</em>");

    // Replace markdown code blocks with html code blocks
    std::regex code_block_regex("```(.*?)```");
    html = std::regex_replace(html, code_block_regex, "<pre><code>$1</code></pre>");

    // Replace markdown images with html images
    std::regex image_regex("!\\[(.*?)\\]\\((.*?)\\)");
    html = std::regex_replace(html, image_regex, "<img src=\"$2\" alt=\"$1\">");

    return html;
}

