```c
#include <stdio.h>
#include <stdlib.h>
#include <time.h>

#define WIDTH 20
#define HEIGHT 20
#define FOOD_SIZE 2

typedef struct {
    int x, y;
    int dir; // 0: up, 1: right, 2: down, 3: left
    int tail[100]; // array to store snake's body
    int tail_len;
} Snake;

Snake snake;
int food_x, food_y;

void init_game() {
    snake.x = WIDTH / 2;
    snake.y = HEIGHT / 2;
    snake.dir = 0;
    snake.tail_len = 0;

    food_x = rand() % WIDTH;
    food_y = rand() % HEIGHT;
}

void update_game() {
    // update snake's position
    switch (snake.dir) {
        case 0:
            snake.y--;
            break;
        case 1:
            snake.x++;
            break;
        case 2:
            snake.y++;
            break;
        case 3:
            snake.x--;
            break;
    }

    // check for collisions
    if (snake.x < 0 || snake.x >= WIDTH || snake.y < 0 || snake.y >= HEIGHT) {
        // game over
        return;
    }

    // check for food collision
    if (snake.x == food_x && snake.y == food_y) {
        snake.tail_len++;
        food_x = rand() % WIDTH;
        food_y = rand() % HEIGHT;
    }

    // check for self-collision
    for (int i = 0; i < snake.tail_len; i++) {
        if (snake.x == snake.tail[i].x && snake.y == snake.tail[i].y) {
            // game over
            return;
        }
    }

    // update snake's tail
    for (int i = snake.tail_len - 1; i > 0; i--) {
        snake.tail[i] = snake.tail[i - 1];
    }
    snake.tail[0] = (Snake){snake.x, snake.y, 0, 0};
}

void draw_game() {
    // clear screen
    system("clear");

    // draw snake
    for (int i = 0; i < snake.tail_len; i++) {
        printf("O");
    }

    // draw food
    printf("F");

    // draw walls
    for (int i = 0; i < WIDTH; i++) {
        printf("#");
    }
    printf("\n");
    for (int i = 0; i < HEIGHT; i++) {
        printf("#");
        for (int j = 0; j < WIDTH; j++) {
            if (i == 0 || i == HEIGHT - 1 || j == 0 || j == WIDTH - 1) {
                printf("#");
            } else {
                printf(" ");
            }
        }
        printf("#\n");
    }
    for (int i = 0; i < WIDTH; i++) {
        printf("#");
    }
    printf("\n");
}

int main() {
    init_game();

    while (1) {
        update_game();
        draw_game();

        // handle input
        char c = getch();
        if (c == 'w') {
            snake.dir = 0;
        } else if (c == 'd') {
            snake.dir = 1;
        } else if (c == 's') {
            snake.dir = 2;
        } else if (c == 'a') {
            snake.dir = 3;
        }
    }

    return 0;
}
```
